const InfiniteCarousel = () => {
    const carouselRef = useRef(null);

    useEffect(() => {
        const carousel = carouselRef.current;
        if (carousel) {
            const firstSet = carousel.children[0].cloneNode(true);
            const secondSet = carousel.children[0].cloneNode(true);
            carousel.appendChild(firstSet);
            carousel.appendChild(secondSet);
        }
    }, []);

    return (
        <div className=" mb-20 flex justify-center items-center font-['JURA']">
            <div className="overflow-hidden w-full">
                <div ref={carouselRef} className="scrolling-wrapper flex">
                    <div className="flex w-full">
                        {images.map(({ icon, name }, index) => (
                            <div key={index} className="w-1/3 mx-3 flex items-center justify-center">
                                {icon}
                                <span className="ml-2">{name}</span>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default InfiniteCarousel;
